package com._2array.day10carrysum;

import java.util.*;

public class AmazingSubArray {
    private final List<Character> possibleChars = List.of('a', 'e', 'i', 'o', 'u', 'A', 'E', 'I', 'O', 'U');

    public static void main(String[] args) {
        System.out.println(new AmazingSubArray().solve3(""));
    }

    public int solve(String A) {
        long count = 0;
        int n = A.length();
        List<Character> possibleChars = Arrays.asList('a', 'e', 'i', 'o', 'u', 'A', 'E', 'I', 'O', 'U');
        for (int i = 0; i < n; i++) {
            if (possibleChars.contains(A.charAt(i))) {
                count = (count + (n - i))% 10003;
            }
        }
        return (int) (count % 10003);
    }

    public int solve3(String A) {
        TreeSet<Character> se = new TreeSet<>();
        se.add('a'); se.add('e'); se.add('i'); se.add('o'); se.add('u');
        se.add('A'); se.add('E'); se.add('I'); se.add('O'); se.add('U');

        int n = A.length();
        long ans = 0;
        for(int i = 0; i < n; i++){
            if(se.contains(A.charAt(i))){
                ans += (n - i);
            }
        }
        return (int)(ans%10003);
    }
    //O(n^2)
    public int solve2(String A){
        long count =0;
        int n = A.length();
        for(int i=0;i<n;i++){
            //if(possibleChars.contains(A.charAt(i))){
            if(A.charAt(i)=='a' || A.charAt(i)=='e' || A.charAt(i)=='i' || A.charAt(i)=='o' || A.charAt(i)=='u' || A.charAt(i)=='A' || A.charAt(i)=='E' || A.charAt(i)=='I' || A.charAt(i)=='O' || A.charAt(i)=='U'){
                for (int j = i; j < n; j++) {
                    count++;
                }
            }
        }
        return (int)count%10003;
    }
}
